export const openModal = () => {
  return dispatch => {
    dispatch({ type: "OPEN_MODAL", showModal: showModal })
  }
}

export const closeModal = () => {
  return dispatch => {
    dispatch({ type: "CLOSE_MODAL", showModal: showModal })
  }
}
