import React from 'react'

const Errors = (props) => {

  const errorDetails = this.props.error.response.data.map(error => (
    <div>
      {error}
    </div>
  ));

  return (
    <div>
      {props.showError && <div className="error-message">Oops!</div>}
    </div>
  )
}

export default Errors;
