import React, { Component } from 'react'
import {Map, Marker, GoogleApiWrapper} from 'google-maps-react';

const API_KEY = process.env.REACT_APP_GOOGLE_API_KEY;

export class MapContainer extends Component {

  constructor() {
    //...
   this.handleWindowResize = debounce(this.handleWindowResize.bind(this), 500);
  }

  componentDidMount() {
      window.addEventListener('resize', this.handleWindowResize);
  }

  handleWindowResize() {
      const nextCenter = this._map.getCenter();
      console.log( 'nextCenter: ' + JSON.stringify(nextCenter) );
      google.maps.event.trigger(this._mapComponent, "resize");
      this.setState({
          center: nextCenter
      });
  }


  render() {

    const style = {
    width: '600px',
    height: '400px',
    border: '.5px solid black'
    }

    const listMarkers = this.props.userCountries.map((country, i) =>(
      <Marker
        key={i}
        name={country.name}
        position={{lat: country.latlng[0], lng: country.latlng[1]}} />
    ));


    return (
      <Map style={style} google={this.props.google} zoom={1}>
        {listMarkers}
      </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: (API_KEY)
})(MapContainer)
