import './static/App.css'
import './static/bootstrap.min.css'
import React, { Component } from 'react'
import { BrowserRouter as Router, Route } from "react-router-dom"
import Layout from './containers/Layout'
import { connect } from 'react-redux'
import { authCheckState } from './actions/authActions'
import { fetchUser, fetchUserTripReports } from './actions/userActions'

class App extends Component {

  /*
  When the App component is mounted, it checks if a user is authenticated and
  fetches the user data from the Django REST API.
  */
  componentWillMount() {
    this.props.authCheckState();
    this.props.fetchUser();
    this.props.fetchUserTripReports(this.props.username);
  }

  render() {
    return (
      <div className="App">
        <Router>
          <Route path="" component={Layout} {...this.props}>
          </Route>
        </Router>
      </div>
    );
  }
}

const mapState = state => {
  return {
    username: state.user.user.username
  }
}

const mapDispatch = dispatch => {
  return {
    authCheckState: () => dispatch(authCheckState()),
    fetchUser: () => dispatch(fetchUser()),
    fetchUserTripReports: (username) => dispatch(fetchUserTripReports(username))
  }
}

export default connect(mapState, mapDispatch)(App);

App.propTypes = {

};
