import React from 'react'
import Card from '@material-ui/core/Card'
import CardHeader from '@material-ui/core/CardHeader'
import CardContent from '@material-ui/core/CardContent'
import CardActions from '@material-ui/core/CardActions'
import Avatar from '@material-ui/core/Avatar'
import Typography from '@material-ui/core/Typography'
import IconButton from '@material-ui/core/IconButton'
import MoreVertIcon from '@material-ui/icons/MoreVert'
import Button from '@material-ui/core/Button'
import ReactModal from 'react-modal'
import Close from '@material-ui/icons/Close'


ReactModal.setAppElement('body');

const TripReportModal = (props) => {

  const listCountries = props.countries.map(country =>(
    <Button onClick={() => props.openCountryModal(country)} size='small' key={country.id}>
      {country.name}
    </Button>
  ));

  return(
    <ReactModal isOpen={props.showTripReportModal}>
      <div className="close-modal">
        <IconButton style={{ float: 'right' }} onClick={props.closePostModal}>
          <Close />
        </IconButton><br/><br/>
      </div><br/>
      <Card>
        <CardHeader
          action={<IconButton><MoreVertIcon /></IconButton>}
          title={props.title}
          subheader={props.author.username}
          avatar={<Avatar src={props.author.home.flag}/>} />
        <CardContent>
          <Typography component="p">
            {props.content}
          </Typography>
        </CardContent>
        <CardActions>
          {listCountries}
          <Button variant="contained" color="primary" onClick={() => this.props.openUpdatePostModal(tripReport)}>Update</Button>
          <Button variant='outlined' color="secondary" onClick={() => this.props.openConfirmDeleteModal(tripReport)}>Delete</Button>
        </CardActions>
      </Card>
    </ReactModal>
  )
};

export default TripReportModal;
