import React, { Component } from 'react'
import Card from '@material-ui/core/Card'
import CardHeader from '@material-ui/core/CardHeader'
import CardContent from '@material-ui/core/CardContent'
import CardActions from '@material-ui/core/CardActions'
import Avatar from '@material-ui/core/Avatar'
import Typography from '@material-ui/core/Typography'
import IconButton from '@material-ui/core/IconButton'
import MoreVertIcon from '@material-ui/icons/MoreVert'
import FavoriteIcon from '@material-ui/icons/Favorite'
import ShareIcon from '@material-ui/icons/Share'
import Button from '@material-ui/core/Button'
import Menu from '@material-ui/core/Menu'
import MenuItem from '@material-ui/core/MenuItem'

class TripReport extends Component {

  state = {
    anchorEl: null,
  };

  handleClick = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {

    const listCountries = [...this.props.countries].sort((a, b) => a.name > b.name).map(country =>(
      <Button onClick={() => this.props.openCountryModal(country)} size='small' key={country.id}>
        {country.name}
      </Button>
    ));

    const { anchorEl } = this.state;

    return(
      <Card>
        <CardHeader
          action={
            <IconButton
              onClick={this.handleClick}
              aria-owns={anchorEl ? 'simple-menu' : undefined}
              aria-haspopup="true"
            >
              <MoreVertIcon />
            </IconButton>}
          title={this.props.title}
          subheader={this.props.author.username}
          avatar={<Avatar src={this.props.author.home.flag}/>} />
        <Menu
          id="simple-menu"
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={this.handleClose}
        >
          <MenuItem onClick={() => {this.handleClose(); this.props.openUserProfileModal(this.props.author);}}>View Profile</MenuItem>
        </Menu>
        <CardContent>
          <Typography component="p">
            {this.props.content}
          </Typography><br/>
          <hr/>
          {listCountries}
        </CardContent>
        <CardActions >
          <IconButton><FavoriteIcon /></IconButton>
          <IconButton><ShareIcon /></IconButton>
        </CardActions>
      </Card>
    )
  }
};

export default TripReport;
