import React, { Component } from 'react'
import { bindActionCreators } from 'redux'
import { connect } from 'react-redux'
import { PropTypes } from 'prop-types'
import CountryModal from '../components/CountryModal'
import TripReport from '../components/TripReport'
import { openCountryModal, closeCountryModal } from '../actions/modalActions'
import { fetchSlugTripReports } from '../actions/tripReportActions'
import { removeError } from '../actions/errorActions'
import { DotLoader } from 'react-spinners'

class Post extends Component {

  componentDidMount () {
    const { slug } = this.props.match.params
    this.props.fetchSlugTripReports(slug);
  }

  componentWillUnmount() {
    this.props.removeError();
  }

  render(){

    return(
      <div className="content">
        {this.props.tripReport && <TripReport tripReport={this.props.tripReport[0]} />}
      </div>
    );
  }
}

const mapState = state => {
  return {
    tripReport: state.tripReport.slugTripReports.results,
  };
}

const mapDispatch = dispatch => {
  return bindActionCreators({
    fetchSlugTripReports,
    removeError
  }, dispatch);
}

export default connect(mapState, mapDispatch)(Post);

Post.propTypes = {
  tripReport: PropTypes.object,
  fetchSlugTripReports: PropTypes.func,
  removeError: PropTypes.func
};
