import React, { Component } from 'react'

class TripReportForm extends Component {
  render(){

    let errorMessage = null;
    if (this.props.error) {
      errorMessage = <p>{this.props.error.message}</p>
    }

    return(
      <div>
        {errorMessage}
        <div className='form-inline'>
          <form onSubmit={this.props.handleSubmit}>
            <input className="form-control user-auth" type='text' name="Title" placeholder="Title"/><br/>
            <input className="form-control user-auth" type='text' name="Content" placeholder="Content"/><br/>
            <button className="btn btn-primary user-auth-btn" type="submit">Post</button>
          </form>
        </div>
      </div>
    )
  }
}

export default TripReportForm;
