import axios from 'axios'

export const authStart = () => {
  return {
    type: "AUTH_START"
  }
}

export const authSucess = token => {
  return {
    type: "AUTH_SUCESS",
    token: token
  }
}

export const authFail = error => {
  return {
    type: "AUTH_FAIL",
    error: error
  }
}

export const authLogout = () => {
  localStorage.removeItem('token');
  localStorage.removeItem('expirationDate');
  return {
    type: "AUTH_LOGOUT"
  }
}

/*
Check to see if the authentication token for the user has expired.
*/
export const checkAuthTimeout = expirationTime => {
  return dispatch => {
    setTimeout(() => {
      dispatch(authLogout());
    }, expirationTime)
  }
}

/*
Login user, setting the token recieved from the backend in the browser
storage with an expiration after 1 hour.
*/
export const authLogin = (username, password) => {
  return dispatch => {
    dispatch(authStart());
    axios.post('http://localhost:8000/api/v1/rest-auth/login/', {
      username: username,
      password: password
    })
    .then(response => {
      const token = response.data.key;
      localStorage.setItem('token', token);
      dispatch(authSucess(token));
    })
    .catch(err => {
      dispatch(authFail(err));
    })
  }
}

export const authRegister = (username, email, password1, password2) => {
  return dispatch => {
    dispatch(authStart());
    axios.post('http://localhost:8000/api/v1/rest-auth/registration/', {
      username: username,
      email: email,
      password1: password1,
      password2: password2
    })
    .then(response => {
      const token = response.data.key;
      const expirationDate = new Date(new Date().getTime() + 3600 * 1000);
      localStorage.setItem('token', token);
      localStorage.setItem('expirationDate', expirationDate);
      dispatch(authSucess(token));
      dispatch(checkAuthTimeout(3600));
    })
    .catch(err => {
      dispatch(authFail(err))
    })
  }
}

export const authCheckState = () => {
  return dispatch => {
    const token = localStorage.getItem('token');
    if (token === undefined) {
      dispatch(logout());
    } else {
      dispatch(authSucess(token));
    }
  }
}
