import * as actionTypes from '../actions/actionTypes'

const initialState = {
  token: null,
  authenticating: false,
  authenticated: false,
  user: {},
  error: null
}

/* Reducer Function*/
export default function (state = initialState, action) {
  switch (action.type) {
    case "LOGIN_REQUEST": {
      return {
        ...state,
        authenticating: true
      }
    }
    case "LOGIN_SUCESS": {
      return {
        ...state,
        authenticating: false,
        authenticated: true,
        user: action.payload
      }
    }
    case "LOGIN_REJECTED": {
      return {
        ...state,
        fetching: false,
        fetched: false,
        error: action.payload
      }
    }
    case "LOGOUT": {
      return {
        ...state,
        fetching: false,
        fetched: false,
        user: {}
      }
    }
    default:
      return state
  }
}
